export default {
  name: "CanvasComp",
  props: {
    canvas: Object,
    bg: Boolean,
    globalCompositeOperation: String
  },
  data: function data() {
    return {
      styleObject: {
        width: "100%",
        height: "100%"
      }
    };
  },
  methods: {
    initCanvas: function initCanvas() {
      var canvas = this.$refs.canvasRef;
      var width = canvas.clientWidth;
      var height = canvas.clientHeight;
      canvas.width = width;
      canvas.height = height;

      if (this.globalCompositeOperation) {
        var context = canvas.getContext("2d");
        context.globalCompositeOperation = this.globalCompositeOperation;
      }

      this.$emit("canvasInited", canvas);
    },
    resize: function resize() {
      var canvas = this.$refs.canvasRef;
      var width = canvas.clientWidth;
      var height = canvas.clientHeight;
      canvas.width = width;
      canvas.height = height;
      this.$emit("canvasResize", {
        width: width,
        height: height
      });
    },
    handleMouseDown: function handleMouseDown(e) {
      this.$emit("canvasMouseDown", e);
    },
    handleMouseMove: function handleMouseMove(e) {
      this.$emit("canvasMouseMove", e);
    }
  },
  created: function created() {
    if (this.bg) {
      this.styleObject = Object.assign(this.styleObject, {
        position: "absolute",
        zIndex: -1,
        top: 0,
        left: 0
      });
    }

    if (this.canvas) {
      this.styleObject = Object.assign(this.styleObject, this.canvas);
    }
  },
  mounted: function mounted() {
    this.initCanvas();
    this.resize = this.resize.bind(this);
    window.addEventListener("resize", this.resize);
  },
  beforeDestroy: function beforeDestroy() {
    window.removeEventListener("resize", this.resize);
  }
};
